filterInputStyles:
border-width: 2px;
border-style: solid;
border-image: linear-gradient(to right, #FFD15C, #EF3E5C, #8b5cf6) 1;
border-radius: 0;
padding: 10px;
width: 75%;

categoriesHeadingStyles:
color: white;
text-align: left;
font-size: 1.5rem;
font-weight: bold;
width: 100%;
margin: 0;
padding-left: 110px;

categoryLinkStyles:
color: #EF3E5C;
background: white;
font-weight: bold;
padding: 4px 8px;
margin: 2px;

productsCtaStyles:
border-radius:0.375rem;
border: 2px dotted #0d51d9;
color:#0d51d9;
font-weight:bold;
padding-top: .5rem;
padding-bottom: .5rem;
background-color: rgba(219, 234, 254, .5);
background: linear-gradient(to bottom right, rgba(219, 234, 254, .5), white, white, rgba(219, 234, 254, .5));
font-size: 1.25rem;
font-style: italic;
text-align: left;

offersCtaStyles:
border-radius:0.375rem;
border: 2px dotted #8b5cf6;
color:#8b5cf6;
font-weight:bold;
padding-top: .5rem;
padding-bottom: .5rem;
background: linear-gradient(to bottom right, rgba(216, 180, 254, .2), white, white, rgba(216, 180, 254, .2));
font-size:1.25rem;
font-style: italic;
text-align: left;

offerPromoCodeStyles:
border: 3px dashed green;
border-radius:0.375rem !important;
background: palegreen;
padding: 5px;
color: green;
letter-spacing: 3px;
font-family: monospace;
font-size: 1.15rem;
width: 200px;
display: block;
margin: 20px 10px 10px 0;
text-align: center;
font-weight: 800;

logoStyles:
background: linear-gradient(to right, #FFD15C, #EF3E5C);
color: transparent;
background-clip: text;
display: inline-block;
font-weight: 400;
letter-spacing:1px;
font-style: italic;
font-size: 2rem;
filter: drop-shadow(2px 2px white);

headerStyles:
background: rgba(255, 255, 255, 0.9); 
backdrop-filter: blur(10px); /* Adjust blur strength as needed */
-webkit-backdrop-filter: blur(2px); /* For Safari support */
border: 1px solid rgba(255, 255, 255, 0.9); /* Light, semi-transparent border */
box-shadow: 0 4px 30px rgba(0, 0, 0, 0.3); /* Subtle shadow for depth */
padding: 1rem 2rem;
